mapper.select:
select  attachment_url  from
t_attachment_record
where business_id = #{getFilePath.businessId,jdbcType=INTEGER}
and   business_type = #{getFilePath.businessType,jdbcType=VARCHAR}
and   business_module = #{getFilePath.businessModule,jdbcType=VARCHAR}
and   attachment_type = #{getFilePath.attachmentType,jdbcType=VARCHAR}
and   company_id = #{companyId,jdbcType=INTEGER}
and   is_delete=0
AND   group_id IN
        (SELECT
            group_id
        FROM
            t_attachment_group
        WHERE
            group_name = #{getFilePath.groupName}
        AND is_delete = 0)
