mapper.update:
update t_user_org_auth uoa,t_user_role ur set uoa.operator_id = #{operatorId,jdbcType=INTEGER},uoa.archive_id = #{acceptArchiveId,jdbcType=INTEGER}
where ur.id = #{id,jdbcType=INTEGER}
and uoa.archive_id = ur.archive_id
and uoa.role_id = ur.role_id
and uoa.is_delete = 0
and ur.is_delete = 0

mapper.select:
select t_user_role.id,t_role.role_id,t_role.role_name,t_role.role_group_id,t_role.is_system_define, t_user_role.is_trusteeship, t_user_role.trusteeship_begin_time, t_user_role.trusteeship_end_time,t_user_role.id user_role_id from t_role,t_user_role where t_user_role.archive_id = #{archiveId,jdbcType=INTEGER} and t_user_role.role_id = t_role.role_id and t_user_role.is_delete = 0
and t_role.company_id = #{companyId,jdbcType=INTEGER}
and t_role.is_delete = 0 ORDER BY t_user_role.id DESC
