    select g.group_id,g.group_name,g.table_id,g.sort
    from t_custom_archive_group g
    where g.group_id in (
      select group_id from t_custom_archive_field f
      WHERE f.is_delete = 0

       and f.table_id = #{tableId,jdbcType=INTEGER}


      and f.table_id in (
