mapper.select:
SELECT

FROM t_archive_career_track t_archive_career_track
where change_id=#{id}

SELECT

FROM t_archive_career_track t_archive_career_track WHERE 1 = 1

    AND t_archive_career_track.archive_id = #{archive_id}


    AND t_archive_career_track.before_user_category LIKE CONCAT('%',#{before_user_category},'%')


    AND t_archive_career_track.after_user_category LIKE CONCAT('%',#{after_user_category},'%')


    AND t_archive_career_track.before_business_unit_id = #{before_business_unit_id}


    AND t_archive_career_track.after_business_unit_id = #{after_business_unit_id}


    AND t_archive_career_track.before_org_id = #{before_org_id}


    AND t_archive_career_track.after_org_id = #{after_org_id}


    AND t_archive_career_track.before_post_id = #{before_post_id}


    AND t_archive_career_track.after_post_id = #{after_post_id}


    AND t_archive_career_track.before_position_id = #{before_position_id}


    AND t_archive_career_track.after_position_id = #{after_position_id}


    AND t_archive_career_track.change_type LIKE CONCAT('%',#{change_type},'%')


    AND t_archive_career_track.change_date = #{change_date}


    AND t_archive_career_track.change_reason LIKE CONCAT('%',#{change_reason},'%')


    AND t_archive_career_track.operator_id = #{operator_id}


    AND t_archive_career_track.create_time = #{create_time}
